import axios from "axios";
import React, { useEffect, useState } from "react";
import keyboardArray from "./Data";

const App = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [videoName, setVideoName] = useState("");
  const [player, setPlayer] = useState(null);
  const [ratingsList, setRatingsList] = useState([]);
  const ratingsListRef = useRef(null);

  const handleSubmit = (e) => {
    e.preventDefault();

    const rating = document.querySelector('input[name="rating"]:checked').value;
    const name = document.getElementById("videoName").textContent;

    const body = {
      name: name,
      rating: rating,
    };

    axios
      .post("http://localhost:4004/ratings", body)
      .then(() => {
        getRatings();
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const getRatings = () => {
    axios
      .get("http://localhost:4004/ratings")
      .then((res) => {
        setRatingsList(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const deleteRating = (id) => {
    axios
      .delete(`http://localhost:4004/ratings/${id}`)
      .then(() => getRatings())
      .catch((err) => console.log(err));
  };

  const changeVideo = () => {
    setCurrentIndex((prevIndex) => (prevIndex + 1) % keyboardArray.length);
  };

  const loadVideoById = (videoId) => {
    player.loadVideoById(videoId);
  };

  const videoNameDisplay = () => {
    setVideoName(keyboardArray[currentIndex].name);
  };

  const onPlayerReady = (event) => {
    event.target.playVideo();
    videoNameDisplay();
  };

  useEffect(() => {
    setPlayer(
      new YT.Player("player", {
        height: "360",
        width: "640",
        videoId: keyboardArray[currentIndex].videoId,
        events: {
          onReady: onPlayerReady,
        },
      })
    );
    console.log("YouTube API ready");

    ratingsListRef.current.addEventListener("click", (event) => {
      if (event.target.classList.contains("delete-btn")) {
        const id = event.target.getAttribute("data-id");
        deleteRating(id);
      }
    });

    getRatings();

    return () => {};
  }, [currentIndex]);

  useEffect(() => {
    if (player) {
      loadVideoById(keyboardArray[currentIndex].videoId);
    }
    videoNameDisplay();
  }, [player, currentIndex]);

  return (
    <div className="App">
      <header>
        <h1>Random Keyboard Sounds</h1>
      </header>
    </div>
  );
};
export default App;
