import React, { useState, useEffect } from "react";
import axios from "axios";
import RatingForm from "./components/RatingForm";
import RatingsList from "./components/RatingsList";

function App() {
  const [ratings, setRatings] = useState([]);
  const [videoName, setVideoName] = useState("");

  useEffect(() => {
    getRatings();
  }, []);

  function getRatings() {
    axios
      .get("http://localhost:4004/ratings")
      .then((res) => {
        setRatings(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  return (
    <div>
      <h1>Random Keyboard Sounds</h1>
      <RatingForm videoName={videoName} refreshRatings={getRatings} />
      <RatingsList ratings={ratings} refreshRatings={getRatings} />
      {/* Here you would also render the VideoPlayer component */}
    </div>
  );
}

export default App;
